import React from 'react'
import './FoundPage.css'
import { Link, NavLink } from 'react-router-dom';

// FoundPage.js
document.addEventListener("DOMContentLoaded", function () {
    // Create container div
    const container = document.createElement("div");
    container.classList.add("container");

    // Create form element
    const form = document.createElement("form");
    form.id = "foundPageForm";

    // Create Location dropdown
    const locationLabel = createLabel("location", "Location:");
    const locationSelect = createDropdown("location", ["Grainger Library", "Campus Instructional Facility (CIF)", "Siebel Center for Design", "Siebel Center for Computer Science", "Activities & Recreation Center (ARC)", "Campus Recreation Center East (CRCE)", "Business Instructional Facility (BIF)", "Illini Union"]);

    // Create Item dropdown
    const itemLabel = createLabel("item", "Item:");
    const itemSelect = createDropdown("item", ["Item 1", "Item 2"]);

    // Create Additional Comments textarea
    const commentsLabel = createLabel("comments", "Additional Comments (max 100 characters):");
    const commentsTextarea = document.createElement("textarea");
    commentsTextarea.id = "comments";
    commentsTextarea.name = "comments";
    commentsTextarea.maxLength = 100;

    // Create Submit button
    const submitButton = document.createElement("button");
    submitButton.type = "button";
    submitButton.textContent = "Submit";
    submitButton.addEventListener("click", submitForm);

    // Append elements to the form
    form.appendChild(locationLabel);
    form.appendChild(locationSelect);
    form.appendChild(itemLabel);
    form.appendChild(itemSelect);
    form.appendChild(commentsLabel);
    form.appendChild(commentsTextarea);
    form.appendChild(submitButton);

    // Append form to the container
    container.appendChild(form);

    // Append container to the body
    document.body.appendChild(container);
  });

  function createLabel(forAttribute, text) {
    const label = document.createElement("label");
    label.htmlFor = forAttribute;
    label.textContent = text;
    return label;
  }

  function createDropdown(id, options) {
    const select = document.createElement("select");
    select.id = id;
    select.name = id;

    options.forEach((optionText) => {
      const option = document.createElement("option");
      option.value = optionText.toLowerCase().replace(/\s/g, "");
      option.textContent = optionText;
      select.appendChild(option);
    });

    return select;
  }

  function submitForm() {
    const location = document.getElementById("location").value;
    const item = document.getElementById("item").value;
    const comments = document.getElementById("comments").value;

    console.log("Location:", location);
    console.log("Item:", item);
    console.log("Additional Comments:", comments);

    document.getElementById("foundPageForm").reset();
  }
